import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './stylesheets/table_of_contents.css'

export function Table_of_contents() {
  const [openMenus, setOpenMenus] = useState({});

  const toggleMenu = (menuName) => {
    setOpenMenus((prevOpenMenus) => ({
      ...prevOpenMenus,
      [menuName]: !prevOpenMenus[menuName],
    }));
  };

  return (
    <div className="hero_Table_of_contents">
        <ul className="toc-menu">
        <li>
            <div className="dropdown_menu">
              <button className="menu__link"
                onClick={() => toggleMenu('overview')}
                aria-expanded={openMenus['overview'] || false}
                aria-controls="overview-submenu"
              >
                Overview {openMenus['overview'] ? '▲' : '▼'}
              </button>
            </div>
            {openMenus['overview'] && (
              <ul id="overview-submenu" className="submenu">
                <li><Link to="/overview/subitem1">Subitem 1</Link></li>
                <li><Link to="/overview/subitem2">Subitem 2</Link></li>
              </ul>
            )}
          </li>
          <li>
            <Link to="/Discover">Discover</Link>
          </li>
          <li>
            <Link to="/Dicern">Discern</Link>
          </li>
          <li>
            <Link to="/Develop">Develop</Link>
          </li>
          <li>
            <Link to="/Demonstrate">Demonstrate</Link>
          </li>
        </ul>
      </div>
  );
}
<div class="dropdown_menu">
  <a class="menu__link menu__link--sublist menu__link--active" 
  href="/prepare/" aria-current="page">Prepare</a>
  <button 
    aria-label="Collapse sidebar category 'Prepare'" 
    aria-expanded="true" type="button" 
    class="clean-btn menu__caret">
  </button>
</div>
export default Table_of_contents;
